sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var start = new GlideDateTime(current.getValue(u_current_date));
	var end = new GlideDateTime(current.getValue(u_when_needed));
	//var dur = new GlideDuration();

	var dur = GlideDateTime.subtract(start, end);

	current.setValue('u_time_resolution', dur);

})(current, previous);
