sys_script_include.script:
var DateCalc = Class.create();
DateCalc.prototype = Object.extendsObject(AbstractAjaxProcessor, {

dateDifference: function() {

var startDate = this.getParameter('sysparm_startDate');
var endDate = this.getParameter('sysparm_endDate');

var gcdt1 = new GlideCalendarDateTime(startDate);
var gcdt2 = new GlideCalendarDateTime(endDate);
var dur = new GlideDuration();

var dur = GlideCalendarDateTime.subtract(gcdt1, gcdt2);
//var daysDifference = dur.getDisplayValue().split(' ')[0];


return daysDifference;

   } 
});
